if(typeof GrowthTracker=="undefined"){var GrowthTracker={};}GrowthTracker.addValidationMessage=function(g,h){var f=document.getElementById("validation_message");var e=document.createElement("p");f.className="alert alert-danger";e.id=g;e.textContent=h;if(!document.getElementById(g)){f.appendChild(e);}};GrowthTracker.removeValidationMessage=function(f){var e=document.getElementById("validation_message");var d=document.getElementById(f);if(d){e.removeChild(d);}if(!e.hasChildNodes()){e.className="";}};GrowthTracker.showError=function(c){var d=c.parentNode;if(d.className.indexOf("has-error")==-1){d.className+=" has-error";}};GrowthTracker.hideError=function(f){var d=f.parentNode;if(d.className.indexOf("has-error")!=-1){var e=/\s?\bhas-error\b/;d.className=d.className.replace(e,"");}};GrowthTracker.validateForm=function(h){var i=document.getElementById(h).elements;var j=true;for(var f=0,g=i.length;f<g;f++){if(i[f]=="SELECT"){if(!i[f].options[i[f].selectedIndex].value){j=false;GrowthTracker.showError(i[f]);}else{GrowthTracker.hideError(i[f]);}}else{if(i[f].validity&&!i[f].validity.valid){j=false;GrowthTracker.showError(i[f]);}else{GrowthTracker.hideError(i[f]);}}}return j;};GrowthTracker.clearForm=function(b){b.reset();GrowthTracker.hideErrors(b);};GrowthTracker.hideErrors=function(f){for(var d=0,e=f.elements.length;d<e;d++){GrowthTracker.hideError(f.elements[d]);GrowthTracker.removeValidationMessage(f.elements[d].id+"_msg");}};GrowthTracker.validateElement=function(b){if(!b){b=this;}else{if(b.srcElement){b=b.srcElement;}}if(b.validity&&!b.validity.valid){GrowthTracker.showError(b);GrowthTracker.addValidationMessage(b.id+"_msg",b.title+" is missing or invalid.");return false;}else{GrowthTracker.hideError(b);GrowthTracker.removeValidationMessage(b.id+"_msg");}return true;};GrowthTracker.validateSelect=function(c,d){if(!c.options[c.selectedIndex].value){GrowthTracker.showError(c);GrowthTracker.addValidationMessage(c.id+"_msg",d);return false;}else{GrowthTracker.hideError(c);GrowthTracker.removeValidationMessage(c.id+"_msg");}return true;};GrowthTracker.toggleVisible=function(b){if(b.style.display=="none"){b.style.display="";}else{b.style.display="none";}};